sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/

function handler({api, event, helpers, imports}) {

    var startTimestamp=api.state.starting_date;
    var endTimestamp=api.state.ending_date;

    //converting to string format

    var startdate=new Date(startTimestamp);
    var enddate=new Date(endTimestamp);
var startyear=String(startdate.getFullYear());
var startmonth=String((startdate.getMonth()+1));
var startday=String(startdate.getDate());
var starthours=String(startdate.getHours());
var startminutes=String(startdate.getMinutes());
var startseconds=String(startdate.getSeconds());


if (startmonth.length< 2) 
        startmonth = '0' + startmonth;
    if (startday.length < 2) 
        startday = '0' + startday;
    if(starthours.length<2)
      starthours='0'+starthours;
    if(startminutes.length<2)
      startminutes='0'+startminutes;
    if(startseconds.length<2)
      startseconds='0'+startseconds;

var endyear=String(enddate.getFullYear());
var endmonth=String((enddate.getMonth()+1));
var endday=String(enddate.getDate());
var endhours=String(enddate.getHours());
var endminutes=String(enddate.getMinutes());
var endseconds=String(enddate.getSeconds());


if (endmonth.length< 2) 
        endmonth = '0' + endmonth;
    if (endday.length < 2) 
        endday = '0' + endday;
    if(endhours.length<2)
      endhours='0'+endhours;
    if(endminutes.length<2)
        endminutes='0'+endminutes;
    if(endseconds.length<2)
      endseconds='0'+endseconds;
// var str= dateFormat.getFullYear()+"-"+  
//         month+"-"+
//         day+" "+
//            hours+":"+
//            minutes+":"+
//            seconds;

// return str;

    // QUERY TO FOLLOW --> "due_dateBETWEENjavascript:gs.dateGenerate('2023-06-01','00:00:00')@javascript:gs.dateGenerate('2023-06-30','23:59:59')";
var queryfilter="due_dateBETWEENjavascript:gs.dateGenerate('"+startyear+"-"+startmonth+"-"+startday+"','"+starthours+":"+startminutes+":"+startseconds+"')@javascript:gs.dateGenerate('"+endyear+"-"+endmonth+"-"+endday+"','"+endhours+":"+endminutes+":"+endseconds+"')";

api.setState('needit_filter',queryfilter);

}
