sys_ux_client_script.script:
function evaluateProperty({api, helpers}) {



  var datesJSONObj=api.data.tltransform_1.output;

  var lengthOfJSONObj=datesJSONObj.length;

  //getting last record due date
  var endtimestamp=api.data.tltransform_1.output[lengthOfJSONObj-1].events[0].timestamp+86400000;

  var dateFormat=new Date(endtimestamp);
  var month=String((dateFormat.getMonth()+1));
  var day=String(dateFormat.getDate());
  var hours=String(dateFormat.getHours());
  var minutes=String(dateFormat.getMinutes());
  var seconds=String(dateFormat.getSeconds());
if (month.length< 2) 
        month = '0' + month;
    if (day.length < 2) 
        day = '0' + day;
    if(hours.length<2)
      hours='0'+hours;
    if(minutes.length<2)
      minutes='0'+minutes;
    if(seconds.length<2)
      seconds='0'+seconds;
var str= dateFormat.getFullYear()+"-"+  
        month+"-"+
        day+" "+
           hours+":"+
           minutes+":"+
           seconds;

return str;

}
