sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {

var startTimestamp=api.state.starting_date;

var dateFormat=new Date(startTimestamp);

var str= "Date: "+ dateFormat.getDate()+   
"/"+(dateFormat.getMonth()+1)+
           "/"+dateFormat.getFullYear()+
           " "+dateFormat.getHours()+
           ":"+dateFormat.getMinutes()+
           ":"+dateFormat.getSeconds();

return "Starting date: "+str;

}
